import React, { useState, useEffect, useRef } from 'react';
import { MapContainer as LeafletMap, TileLayer, Marker, Popup, useMapEvents } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix Leaflet icon issue
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
});

const mapContainerStyle = {
  width: '100%',
  height: '500px',
};

const defaultCenter = {
  lat: 23.8103, // Default center (Dhaka, Bangladesh)
  lng: 90.4125,
};

// Component to handle map events
function MapEvents({ onLocationSelect }) {
  const map = useMapEvents({
    click: (e) => {
      const { lat, lng } = e.latlng;
      if (onLocationSelect) {
        onLocationSelect({ lat, lng });
      }
    },
  });
  return null;
}

const MapComponent = ({ onLocationSelect }) => {
  const [position, setPosition] = useState(null);
  const [searchAddress, setSearchAddress] = useState("");
  const markerRef = useRef(null);

  // Try to get user's current location
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setPosition({
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          });
        },
        () => {
          // If user denies location access, use default
          setPosition(defaultCenter);
        }
      );
    } else {
      setPosition(defaultCenter);
    }
  }, []);

  const handleLocationSelect = (location) => {
    setPosition(location);
    if (onLocationSelect) {
      onLocationSelect(location);
    }
  };

  const handleMarkerDragEnd = (e) => {
    const { lat, lng } = e.target.getLatLng();
    setPosition({ lat, lng });
    if (onLocationSelect) {
      onLocationSelect({ lat, lng });
    }
  };

  const handleSearchAddress = async () => {
    if (searchAddress.trim() === "") return;

    try {
      // Using Nominatim for geocoding (free and open-source)
      const response = await fetch(`https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(searchAddress)}`);
      const data = await response.json();

      if (data && data.length > 0) {
        const location = {
          lat: parseFloat(data[0].lat),
          lng: parseFloat(data[0].lon),
        };

        setPosition(location);
        if (onLocationSelect) {
          onLocationSelect(location);
        }
      } else {
        alert("Location not found. Please try a different search term.");
      }
    } catch (error) {
      console.error("Error searching for location:", error);
      alert("Error searching for location. Please try again.");
    }
  };

  if (!position) {
    return <div>Loading map...</div>;
  }

  return (
    <div>
      <div className="search-container" style={{ marginBottom: '10px' }}>
        <input
          type="text"
          placeholder="Search for a location"
          value={searchAddress}
          onChange={(e) => setSearchAddress(e.target.value)}
          style={{ width: '70%', padding: '8px' }}
        />
        <button
          onClick={handleSearchAddress}
          style={{
            padding: '8px 15px',
            marginLeft: '10px',
            backgroundColor: '#3498db',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Search
        </button>
      </div>

      <LeafletMap
        center={[position.lat, position.lng]}
        zoom={13}
        style={mapContainerStyle}
      >
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <MapEvents onLocationSelect={handleLocationSelect} />
        {position && (
          <Marker
            position={[position.lat, position.lng]}
            draggable={true}
            eventHandlers={{
              dragend: handleMarkerDragEnd,
            }}
            ref={markerRef}
          >
            <Popup>
              <div>
                <h3>Selected Location</h3>
                <p>Latitude: {position.lat.toFixed(6)}</p>
                <p>Longitude: {position.lng.toFixed(6)}</p>
              </div>
            </Popup>
          </Marker>
        )}
      </LeafletMap>
    </div>
  );
};

export default MapComponent;
