import React, { useState } from 'react';
import { Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';
import MapWrapper from './MapWrapper';

// Fix Leaflet icon issue
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.7.1/dist/images/marker-shadow.png',
});

const mapContainerStyle = {
  width: '100%',
  height: '500px',
};

const defaultCenter = {
  lat: 23.8103, // Default center (Dhaka, Bangladesh)
  lng: 90.4125,
};

const ItemList = ({ items }) => {
  const [selectedItem, setSelectedItem] = useState(null);

  if (items.length === 0) {
    return <p>No locations found. Add some locations to get started!</p>;
  }

  // Calculate map center based on all locations
  const calculateCenter = () => {
    if (items.length === 0 || !items[0].location) return defaultCenter;

    if (items.length === 1) {
      return {
        lat: items[0].location.lat,
        lng: items[0].location.lng
      };
    }

    // Calculate the average of all locations
    const sumLat = items.reduce((sum, item) => sum + (item.location ? item.location.lat : 0), 0);
    const sumLng = items.reduce((sum, item) => sum + (item.location ? item.location.lng : 0), 0);

    return {
      lat: sumLat / items.length,
      lng: sumLng / items.length
    };
  };

  const center = calculateCenter();

  return (
    <div>
      <h2>Saved Locations</h2>

      <div id="item-list-map-container">
        <MapWrapper
          center={[center.lat, center.lng]}
          zoom={10}
          style={mapContainerStyle}
          id="item-list-map"
        >

        {items.map(item => {
          if (!item.location) return null;

          return (
            <Marker
              key={item._id}
              position={[item.location.lat, item.location.lng]}
              onClick={() => setSelectedItem(item)}
            >
              <Popup>
                <div>
                  <h3>{item.name}</h3>
                  <p>{item.description}</p>
                  <p>Added on: {new Date(item.date).toLocaleDateString()}</p>
                  <p>
                    <strong>Coordinates:</strong> {item.location.lat.toFixed(6)}, {item.location.lng.toFixed(6)}
                  </p>
                </div>
              </Popup>
            </Marker>
          );
        })}
      </MapWrapper>
      </div>

      <div className="location-list-container" style={{ marginTop: '20px' }}>
        <h3>Location List</h3>
        <ul className="item-list">
          {items.map(item => (
            <li
              key={item._id}
              className="item"
              onClick={() => setSelectedItem(item)}
              style={{ cursor: 'pointer' }}
            >
              <h3>{item.name}</h3>
              <p>{item.description}</p>
              {item.location && (
                <p>
                  <strong>Coordinates:</strong> {item.location.lat.toFixed(6)}, {item.location.lng.toFixed(6)}
                </p>
              )}
              <p>Added on: {new Date(item.date).toLocaleDateString()}</p>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ItemList;
